def help_message() -> str:
    """Return a list of commands"""
    return "List of commands:\n" + "!date <location> - Returns the date in the city proceeding the command\n" + "!directions <starting point> <destination> - Returns a list of directions from a starting location to a destination\n" + "!joke - Returns a joke\n" + "!news <location> - Returns a list of news articles relevant to a location\n" + "!time <location> - Returns the time in the city proceeding the command\n" + "!translate <text> - Translates the text proceeding the command\n" + "!weather <location> - Returns the weather in the city proceeding the command\n" + "<text> - Returns a list of results from a Google search\n"